<?phperror_reporting(-1);ini_set('display_errors', 1);$root	   = str_replace('application', '', __DIR__);$paths 	   = array();// Models$paths[]   = sprintf('%smodel', $root);$paths[]   = sprintf('%sapplication', $root);$paths[]   = sprintf('%stest', $root);$paths[]   = get_include_path();function autoload($className){	switch( $className )	{		// Password hashing		case 'PasswordHash':			require sprintf('%s/application/thirdparty/phpass/PasswordHash.php', 							str_replace('application', '', __DIR__));			return true;				// OpenID 		case 'LightOpenID':			require sprintf('%s/application/thirdparty/lightopenid/openid.php', 							str_replace('application', '', __DIR__));			return true;					// Everything else		default:			require sprintf('%s.class.php', $className);			return true;	}}// Add your class dir to include pathset_include_path(implode(PATH_SEPARATOR, $paths));// You can use this trick to make autoloader look for commonly used "My.class.php" type filenamesspl_autoload_extensions('.class.php,.php');// Use default autoload implementationspl_autoload_register('autoload');