<?phpclass Router{	const defaultController = 'Index';	const defaultAction 	= 'Index';		public function __construct()	{	}		public function Route($URI)	{		$parts 	 	 = parse_url($URI);		$defaultPath = '/';		$method		 = strtoupper($_SERVER['REQUEST_METHOD']);		$path  	 	 = isset($parts['path']) ? trim($parts['path'], '/') : '/';					$action 	 = $defaultAction;		$controller  = $defaultController;		$id			 = 0;				if( $path != $defaultPath )		{			$parts = explode('/', $path);						if( count($parts) > 1 )			{				$controller = $parts[0];				$action		= $parts[1];				$id			= $this->FindID($parts);			}		}				return array('method' 	  => $method,					 'controller' => $controller,					 'action'	  => $action,					 'id'		  => $id);	}		public function FindID($parts)	{		if( $parts && is_array($parts) )		{			$reversedParts = array_reverse($parts);						foreach( $reversedParts as $p )			{				$mightBeAnID = intval($p);								if( $mightBeAnID > 0 )				{					return $mightBeAnID;				}			}		}				return false;	}}