<?php/* * Stashbox - data storage mechanism * */class Stashbox{	protected $connections = array();		// @param array $connections	public function SetConnections($connections)	{		$this->connections = $connections;		return true;	}		// @param string $name - name of the connection to retrieve	// if none provided, will get the first in the array	public function GetConnection($name = '')	{		return $name && isset($this->connections[$name]) ? $this->connections[$name] : $this->GetDefaultConnection();	}		// Gets the first connection 	public function GetDefaultConnection()	{		return isset($this->connections[0]) && $this->connections[0] ? $this->connections[0] : false;	}		// @param object - searches object for primary key property	// if isset and > 0, then this is NOT a new record	public function IsNew($object)	{		return isset($object->id) && intval($object->id) > 0 ? false : true;	}		public function GetTableName($object)	{		return is_object($object) ? strtolower(get_class($object)) : false;	}}