<?php/* * Util - various misc functions * */namespace application;class Util{	public static function FlattenObjArray(array $input, $property)	{		if( $input )		{			$output = array();			foreach( $input as $k => $o )			{				if( isset($o->{$property}) )				{					foreach( $o as $key => $value )					{						if( $key == $property )						{							$output[] = $o->{$property};						}					}				}			}						return $output;		}				return $input;	}		// http://stackoverflow.com/questions/3958226/using-put-method-with-php-curl-library/7125558#7125558	public static function GetPutData()	{		$body = 'the RAW data string I want to send';		/** use a max of 256KB of RAM before going to disk */		$fp = fopen('php://temp/maxmemory:256000', 'w');				if (!$fp) 		{			return false;		}				fwrite($fp, $body);		fseek($fp, 0); 		curl_setopt($ch, CURLOPT_BINARYTRANSFER, true);		curl_setopt($ch, CURLOPT_INFILE, $fp); // file pointer		curl_setopt($ch, CURLOPT_INFILESIZE, strlen($body));  		curl_close($ch);	}		public static function Esc($input)	{		return htmlentities($input, ENT_QUOTES, 'UTF-8');	}		/**	 * http://snipplr.com/view.php?codeview&id=22741	 * Modifies a string to remove al non ASCII characters and spaces.	 */	public static function Slugify($text)	{		// replace non letter or digits by -		$text = preg_replace('~[^\\pL\d]+~u', '-', $text);	 		// trim		$text = trim($text, '-');	 		// transliterate		if (function_exists('iconv'))		{			$text = iconv('utf-8', 'us-ascii//TRANSLIT', $text);		}				// lowercase		$text = strtolower($text);	 		// remove unwanted characters		$text = preg_replace('~[^-\w]+~', '', $text);	 		if (empty($text))		{			return 'n-a';		}	 		return $text;	}}