<?php class Bug extends Model{	public function __construct($connection)	{		parent::__construct($connection);	}		public function Update($objBug)	{		$q    = 'UPDATE bug				 SET title 		 = :title,					 description = :description,					 updated_at  = NOW(),					 user_id     = :userID			     WHERE id 		 = :id';				 		$params = array(':title' 		=> $objBug->title,					    ':description'  => $objBug->description,					    ':userID' 		=> $objBug->userID,					    ':id'			=> $objBug->id);			return $this->Save($q, $params);	}		public function Add($objBug)	{		$q      = 'INSERT INTO bug(title, 								   description, 								   created_at, 								   user_id)				   VALUES(:title, 						  :description, 						  NOW(), 						  :userID)';				   		$params = array(':title' 		=> $objBug->title,						':description'  => $objBug->description,						':userID' 		=> $objBug->userID);								return $this->Save($q, $params);	}		public function GetBugByID($bugID)	{		$q    = 'SELECT b.id,						b.title,						b.description						b.created_at				 FROM bug b				 WHERE b.id = :bugID';				 		return $this->Fetch($q, array(':bugID' => $bugID));	}		public function GetBugs()	{		$q    = 'SELECT b.id,						b.title,						b.description						b.created_at				 FROM bug b';				 		return $this->FetchAll($q);	}}