<?php/* * Model - Parent object for all models * */abstract class Model{	public $connection;	public function __construct($connection)	{		$this->connection = $connection;	}		public function FetchAll($query, $params)	{		try		{			$stmt = $this->Execute($query, $params);			return $stmt->FetchAll();		}		catch(PDOException $e)		{			echo $e->getMessage();		}	}		public function Fetch($query, $params = array())	{		try		{			$stmt = $this->Execute($query, $params);			return $stmt->Fetch();		}		catch(PDOException $e)		{			echo $e->getMessage();		}	}		public function Execute($query, $params)	{		$stmt 	 		= $this->connection->prepare($query);		$success 		= $stmt->execute($params);		return $success ? $stmt : false;	}		public function Save($query, $params = array())	{		$stmt 		  = $this->Execute($query, $params);		$updateResult = $stmt || ($stmt && $stmt->rowCount() > 0);		$insertResult = $this->connection->lastInsertId();				return $this->IsInsertQuery($query) ? $insertResult : $updateResult;	}		public function IsInsertQuery($query)	{		return strpos(strtolower($query), 'insert') === 0;	}}