<?phpclass User extends Model{	public function __construct($connection)	{		parent::__construct($connection);	}		public function Update($objUser)	{		$q    = 'UPDATE user				 SET 	password 	 = :password			     WHERE id 		 = :id';		return $this->Save($q, array(':title' => $objUser->password,									 ':id'	   => $objUser->id));	}		public function Add($objUser)	{		$q    = 'INSERT INTO user(login, password, created_at)			     VALUES(:login, :password, NOW())';		return $this->Save($q, array(':login' 		=> $objUser->login,									 ':password' 	=> $objUser->password));	}		public function GetUserByID($userID)	{		$q    = 'SELECT u.login,						u.created_at				 FROM user u				 WHERE u.id = :userID';		return $this->Fetch($q, array(':userID' => $userID));	}		public function GetUsers()	{		$q    = 'SELECT u.login,						u.created_at				 FROM user u';		return $this->FetchAll($q);	}}