<?php/* * PasswordHashTest - test functionality of class loader * */class PasswordHashTest extends PHPUnit_Framework_TestCase{	public function testHash()	{		$password = 'Biggie Smalls Is Da Illest';		$objHash  = new PasswordHash(8, false);		$hash     = $objHash->HashPassword($password);				$this->assertTrue( strlen($hash) > 20 );				$passwordMatch = $objHash->CheckPassword($password, $hash);				$this->assertTrue($passwordMatch);	}}