<?php/* * RouterTest - test functionality of the URI router * */class RouterTest extends PHPUnit_Framework_TestCase{		protected $backupGlobals = FALSE;		public function testRoute()	{		$request  = 'POST http://test.com/page/update/1';				$expected = array('method'     => 'POST',						  'controller' => 'page',						  'action' 	   => 'update',						  'id'		   => 1);				$this->objRouter			 ->expects($this->any())			 ->method('Route')			 ->with($request)			 ->will($this->returnValue($expected));				$actual = $this->objRouter->Route($request);				$this->assertSame($expected, $actual);	}	public function setUp()	{		$this->objRouter = $this->getMock('Router');	}}	